	// 获取用户资料
	public void getUserInfo() {
		InputStream is = null;
		try {
			is = UserMain.this.openFileInput("information");
			ByteArrayOutputStream boStream = new ByteArrayOutputStream();
			byte[] buffer = new byte[1024];
			int len = -1;
			while ((len = is.read(buffer)) != -1) {
				boStream.write(buffer, 0, len);
			}
			String[] info = boStream.toString().split("@");
			nameString = info[0];
			majorString = info[1];
			classString = info[2];
			idString = info[3];
			Log.v("提示：", "用户资料读取成功");
		} catch (Exception e) {
			// TODO: handle exception
			Log.v("提示：", "无法读取本地文件");
		}
	}

