	// 查询
	public void execute(final List<NameValuePair> nameValuePairs) {
		new Thread(new Runnable() {

			@Override
			public void run() {
				// TODO 自动生成的方法存根
				while (executeFlag) {
					Message msg = myHandler.obtainMessage();
					Bundle bundle = new Bundle();
					Looper.prepare();//
					String SessionIDString = null;
					String VIEWSTATE = null;
					for (int i = 0; i < mCookies.size(); i++) {

						SessionIDString = mCookies.get(i).getName() + "="
								+ mCookies.get(i).getValue();
					}
					try {
						BasicHttpParams httpParams = new BasicHttpParams();
						HttpConnectionParams.setConnectionTimeout(httpParams,
								8 * 1000); // 请求超时
						HttpConnectionParams
								.setSoTimeout(httpParams, 10 * 1000); // 等待数据超时
						// 添加新的HttpClient
						HttpClient httpclient = new DefaultHttpClient(
								httpParams);

						// httpget获取VIEWSTATE值
						HttpGet httpget = new HttpGet(
								"http://jw.fdygxy.cn:8081/xscj.aspx?xh="
										+ userID);
						httpget.setHeader("Cookie", SessionIDString);
						HttpResponse res = httpclient.execute(httpget);
						// 发送GET,并返回一个HttpResponse对象
						String result = EntityUtils.toString(res.getEntity());// 获取返回结果
						String reg = "<input[^<>]*?\\svalue=['\"]?(.*?)['\"]?\\s.*?>";// 正则表达式获取VIEWSTATE的varlue值
						Matcher m = Pattern.compile(reg).matcher(result);
						while (m.find()) {
							VIEWSTATE = m.group(1);
							// Log.v("提示：",VIEWSTATE);
							break;
						}
						System.out.print(nameString);
						// post获取成绩
						HttpPost httppost = new HttpPost(
								"http://jw.fdygxy.cn:8081/xscj.aspx?xh="
										+ userID);
						nameValuePairs.add(new BasicNameValuePair(
								"__VIEWSTATE", VIEWSTATE));
						httppost.setEntity(new UrlEncodedFormEntity(
								nameValuePairs, "gb2312"));
						httppost.addHeader("Cookie", SessionIDString);
						HttpResponse response = httpclient.execute(httppost);

						// 服务器返回的数据
						String responseString = null;
						int code = response.getStatusLine().getStatusCode();
						if (response.getEntity() != null
								&& code == HttpStatus.SC_OK) {
							responseString = EntityUtils.toString(response
									.getEntity());
							// 返回服务器响应的HTML代码
							// Log.v("提示：",responseString);

							// 使用正则表达式替换掉无关内容
							responseString = responseString.replaceAll(
									"<table id=\"kk\"[\\w\\W]+?</table>", "")
									.replaceAll(
											"<table height[\\w\\W]+?</table>",
											"");
							// 把html值传给webview显示
							MyWebview.content = responseString;
							bundle.putInt("res", 1);
							bundle.putInt("state", 1);
						}
					} catch (ConnectTimeoutException e) {// 请求超时
						e.printStackTrace();
						bundle.putInt("state", 2);
					} catch (SocketTimeoutException e) {// 返回数据超时
						e.printStackTrace();
						bundle.putInt("state", 3);
					} catch (UnknownHostException e) {// 未知地址，表示程序当前无法访问域名，网络故障
						e.printStackTrace();
						bundle.putInt("state", 4);

					} catch (ClientProtocolException e) {// 提交给服务器的请求不符合HTTP协议，cookie过期
						e.printStackTrace();
						bundle.putInt("state", 5);
					} catch (Exception e) {
						e.printStackTrace();
						bundle.putInt("state", 0);
						bundle.putString("exception", e.toString());
					} finally {
						msg.setData(bundle);
						myHandler.sendMessage(msg);
						executeFlag = false;
					}

				}
			}
		}).start();
	}

